pair a = (-2 * Cos(70), 0);
pair b = (1, 0);
pair c = dir(30);
pair d = dir(110);
pair e = (0, 0);
draw(a--d--e--cycle);
draw(e--b--c--cycle);

// Labels
label("$A$", a, W); label("$B$", b, E);
