pair A, B, C, D, E, F, X, Y, Z;
A=(0,0);
B=(12,0);
C=(6,-6);
D=(6,-22);
E=(0,-16);
F=(12,-16);
X=(A+C)/2;
Y=(B+C)/2;
Z=(C+D)/2;
