defaultpen(fontsize(11));

pair b = (0, 0);
pair d = 7 * dir(-30);
pair a = 8 * dir(-140);
pair c = 6 * dir(-90);

draw(a--b--d--c--cycle);
draw(b--c);

label("$y^\circ$", a, 2 * (E + NE));
label("$y^\circ$", b, 2 * (S + SW));
label("$x^\circ$", b, 2 * (S + SE));
label("$x^\circ$", d, 2 * (2 * W));

label("$A$", a, W);
label("$B$", b, N);
label("$D$", d, E);
label("$C$", c, S);

[/asy]
