pair A, B, C, D, E, F, H;

B = (0,0);
C = (4,0);
A = extension(B, B + dir(49), C, C + dir(180 - 12));
D = (A + reflect(B,C)*(A))/2;
E = (B + reflect(C,A)*(B))/2;
F = (C + reflect(A,B)*(C))/2;
H = extension(B,E,C,F);

draw(B--H--C--cycle);
draw(H--D);
draw(B--F);
draw(C--E);

