defaultpen(linewidth(1pt)+fontsize(10pt));

pair A,B,C,D,E;
A = (0,0);
B = dir(108);
C = B+dir(39);
D = C+dir(-39);
E = (1,0);

draw(A--B--C--D--E--cycle,linewidth(1));
