[asy]

unitsize(2 cm);

pair A, B, C, D, E, O, P, Q, R;

A = dir(90);

B = dir(90 - 360/5);

C = dir(90 - 2*360/5);

D = dir(90 - 3*360/5);

E = dir(90 - 4*360/5);

