draw((4.3,0)--(4.3,-0.3)--(4,-0.3));
draw((11.7,0)--(11.7,-0.3)--(12,-0.3));
fill(Circle((2,-2),2),white);
fill(Circle((8,-4),4),white);
draw(Circle((2,-2),2));
draw(Circle((8,-4),4));
dot((2,-2));
dot((8,-4));
[/asy]
