xaxis(Arrows(4));
yaxis(ymin=-2,Arrows(4));
pair A=(-1,12), B=(7,12), C=(-1,3), D=(7,3);
pair[] dots = {A,B,C,D};
dot(dots);
draw(A--B--D--C--cycle);
label("$8$",(A+B)/2,N);
label("$y-3$",(B+D)/2,E);
[/asy]
