defaultpen(linewidth(0.7));
real x=8;
pair A=(0,0), B=(x,2*x), C=(x,0);
pair[] dots = {A,B,C};
dot(dots);
draw(A--B--C--cycle);
xaxis(-2,10,Arrows(4));
yaxis(-2,20,Arrows(4));
label("$(x,0)$",C,S);
label("$(x,2x)$",B,N);
[/asy]
