defaultpen(linewidth(0.7)+fontsize(10));
draw((0,0)--(8,0));
for(int i = 0; i < 9; ++i){
draw((4,10)--(i,0));
if(i>=4)

label("$B_"+string(i-4)+"$",(i,0),S);
}
label("$A$",(4,10),N);
