P = rotate(225)*A;
D = foot(P,A,-A);
draw(O--P--D);
draw(rightanglemark(O,D,P,2));
draw(Circle(O,1));
label("$O$",O,NE);
label("$P$",P,SW);
//label("$A$",A,SE);
label("$D$",D,N);
[/asy]

