label("$B$",(-x,2+2x),NW); label("$D$",(2+x,2+2x),NE); label("$E$",(2+x,0),SE); label("$F$",(-x,0),SW);
label("$A$",(-x,x+2),W); label("$C$",(0,2+2x),N);
[/asy]
